mapper.select:
SELECT b.*,
c.name AS categoryName

ORDER BY b.id DESC
LIMIT #{fetchCnt}


        OFFSET #{offset}


        OFFSET 0

SELECT b.*, c.name AS categoryName
FROM tb_board b
         LEFT JOIN tb_category c ON b.categoryCode = c.id
WHERE b.useYn = 'Y'
  AND b.id = #{id}
ORDER BY b.id DESC;

mapper.update:
UPDATE tb_board
SET viewCnt = viewCnt + 1
WHERE id = #{id};

mapper.select:
SELECT * FROM board_attach
WHERE boardId = #{id}
  AND useYn = 'Y'
ORDER BY id DESC;

SELECT * FROM board_attach
WHERE storedFileName = #{name}
  AND useYn = 'Y'
ORDER BY id DESC;
