mapper.select:
SELECT COUNT(*) as totalCount FROM board WHERE useYn = 'Y' AND createdAt BETWEEN CONCAT(#{fromDt}, ' 00:00:00') AND CONCAT(#{toDt}, ' 23:59:59')
AND categoryCode = #{categoryId}
AND ( title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%') OR writer LIKE CONCAT('%', #{keyword}, '%') )

SELECT b.*, c.name AS categoryName FROM board b LEFT JOIN category c ON b.categoryCode = c.id WHERE b.useYn = 'Y' AND b.createdAt BETWEEN CONCAT(#{fromDt}, ' 00:00:00') AND CONCAT(#{toDt}, ' 23:59:59')
AND b.categoryCode = #{categoryId}
AND ( b.title LIKE CONCAT('%', #{keyword}, '%') OR b.content LIKE CONCAT('%', #{keyword}, '%') OR b.writer LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY b.id DESC LIMIT #{offset}, #{fetchCnt}

SELECT b.*, c.name AS categoryName
FROM board b
         LEFT JOIN category c ON b.categoryCode = c.id
WHERE b.useYn = 'Y'
AND b.id = #{id}
ORDER BY b.id DESC;

mapper.update:
UPDATE board
SET viewCnt = viewCnt + 1
WHERE id = #{id};
